WITH date_keys AS MATERIALIZED (SELECT d1.d_date_sk, d1.d_date FROM date_dim d1 INNER JOIN date_dim d2 ON d1.d_month_seq = d2.d_month_seq WHERE d2.d_date IN ('2002-02-26','2002-05-03','2002-08-19','2002-11-18')), store_returns_agg AS (SELECT i.i_item_id AS item_id, SUM(sr.sr_return_quantity) AS sr_item_qty FROM store_returns sr INNER JOIN date_keys dk ON sr.sr_returned_date_sk = dk.d_date_sk INNER JOIN item i ON sr.sr_item_sk = i.i_item_sk WHERE i.i_category IN ('Home', 'Men') AND i.i_manager_id BETWEEN 8 AND 17 AND sr.sr_reason_sk IN (6, 7, 25, 45, 51) AND sr.sr_return_amt / NULLIF(sr.sr_return_quantity, 0) BETWEEN 237 AND 266 GROUP BY i.i_item_id), catalog_returns_agg AS (SELECT i.i_item_id AS item_id, SUM(cr.cr_return_quantity) AS cr_item_qty FROM catalog_returns cr INNER JOIN date_keys dk ON cr.cr_returned_date_sk = dk.d_date_sk INNER JOIN item i ON cr.cr_item_sk = i.i_item_sk WHERE i.i_category IN ('Home', 'Men') AND i.i_manager_id BETWEEN 8 AND 17 AND cr.cr_reason_sk IN (6, 7, 25, 45, 51) AND cr.cr_return_amount / NULLIF(cr.cr_return_quantity, 0) BETWEEN 237 AND 266 GROUP BY i.i_item_id), web_returns_agg AS (SELECT i.i_item_id AS item_id, SUM(wr.wr_return_quantity) AS wr_item_qty FROM web_returns wr INNER JOIN date_keys dk ON wr.wr_returned_date_sk = dk.d_date_sk INNER JOIN item i ON wr.wr_item_sk = i.i_item_sk WHERE i.i_category IN ('Home', 'Men') AND i.i_manager_id BETWEEN 8 AND 17 AND wr.wr_reason_sk IN (6, 7, 25, 45, 51) AND wr.wr_return_amt / NULLIF(wr.wr_return_quantity, 0) BETWEEN 237 AND 266 GROUP BY i.i_item_id) SELECT sra.item_id, sra.sr_item_qty, sra.sr_item_qty/(sra.sr_item_qty+cra.cr_item_qty+wra.wr_item_qty)/3.0 * 100 AS sr_dev, cra.cr_item_qty, cra.cr_item_qty/(sra.sr_item_qty+cra.cr_item_qty+wra.wr_item_qty)/3.0 * 100 AS cr_dev, wra.wr_item_qty, wra.wr_item_qty/(sra.sr_item_qty+cra.cr_item_qty+wra.wr_item_qty)/3.0 * 100 AS wr_dev, (sra.sr_item_qty+cra.cr_item_qty+wra.wr_item_qty)/3.0 AS average FROM store_returns_agg sra INNER JOIN catalog_returns_agg cra ON sra.item_id = cra.item_id INNER JOIN web_returns_agg wra ON sra.item_id = wra.item_id ORDER BY sra.item_id, sra.sr_item_qty LIMIT 100