WITH filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_city = 'Jackson'), filtered_income_band AS (SELECT ib_income_band_sk FROM income_band WHERE ib_lower_bound >= 23567 AND ib_upper_bound <= 73567), filtered_household_demographics AS (SELECT hd_demo_sk, hd_income_band_sk FROM household_demographics hd JOIN filtered_income_band fib ON hd.hd_income_band_sk = fib.ib_income_band_sk), filtered_customer AS (SELECT c.c_customer_id, c.c_first_name, c.c_last_name, c.c_current_cdemo_sk FROM customer c JOIN filtered_customer_address fca ON c.c_current_addr_sk = fca.ca_address_sk JOIN filtered_household_demographics fhd ON c.c_current_hdemo_sk = fhd.hd_demo_sk), filtered_customer_demographics AS (SELECT cd_demo_sk FROM customer_demographics) SELECT fc.c_customer_id AS customer_id, COALESCE(fc.c_last_name, '') || ', ' || COALESCE(fc.c_first_name, '') AS customername FROM filtered_customer fc JOIN filtered_customer_demographics fcd ON fc.c_current_cdemo_sk = fcd.cd_demo_sk JOIN store_returns sr ON sr.sr_cdemo_sk = fcd.cd_demo_sk ORDER BY fc.c_customer_id LIMIT 100