WITH filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_city = 'Mount Vernon'), filtered_income_band AS (SELECT ib_income_band_sk FROM income_band WHERE ib_lower_bound >= 40374 AND ib_upper_bound <= 90374), composite_dimension AS (SELECT hd.hd_demo_sk, fca.ca_address_sk FROM filtered_customer_address fca INNER JOIN household_demographics hd ON 1=1 INNER JOIN filtered_income_band fib ON hd.hd_income_band_sk = fib.ib_income_band_sk), store_returns_with_cdemo AS (SELECT sr.sr_cdemo_sk, cd.cd_demo_sk FROM store_returns sr INNER JOIN customer_demographics cd ON sr.sr_cdemo_sk = cd.cd_demo_sk) SELECT c.c_customer_id AS customer_id, COALESCE(c.c_last_name, '') || ', ' || COALESCE(c.c_first_name, '') AS customername FROM store_returns_with_cdemo srcd INNER JOIN composite_dimension cd ON srcd.cd_demo_sk = cd.cd_demo_sk INNER JOIN customer c ON c.c_current_addr_sk = cd.ca_address_sk AND c.c_current_hdemo_sk = cd.hd_demo_sk AND c.c_current_cdemo_sk = srcd.cd_demo_sk ORDER BY c.c_customer_id LIMIT 100