WITH store_results AS (SELECT DISTINCT c.c_last_name, c.c_first_name, dd.d_date FROM store_sales ss INNER JOIN date_dim dd ON ss.ss_sold_date_sk = dd.d_date_sk INNER JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk WHERE dd.d_month_seq BETWEEN 1213 AND 1224 AND ss.ss_list_price BETWEEN 168 AND 197 AND c.c_birth_year BETWEEN 1968 AND 1974 AND ss.ss_wholesale_cost BETWEEN 76 AND 86), catalog_results AS (SELECT DISTINCT c.c_last_name, c.c_first_name, dd.d_date FROM catalog_sales cs INNER JOIN date_dim dd ON cs.cs_sold_date_sk = dd.d_date_sk INNER JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk WHERE dd.d_month_seq BETWEEN 1213 AND 1224 AND cs.cs_list_price BETWEEN 168 AND 197 AND c.c_birth_year BETWEEN 1968 AND 1974 AND cs.cs_wholesale_cost BETWEEN 76 AND 86), web_results AS (SELECT DISTINCT c.c_last_name, c.c_first_name, dd.d_date FROM web_sales ws INNER JOIN date_dim dd ON ws.ws_sold_date_sk = dd.d_date_sk INNER JOIN customer c ON ws.ws_bill_customer_sk = c.c_customer_sk WHERE dd.d_month_seq BETWEEN 1213 AND 1224 AND ws.ws_list_price BETWEEN 168 AND 197 AND c.c_birth_year BETWEEN 1968 AND 1974 AND ws.ws_wholesale_cost BETWEEN 76 AND 86) SELECT COUNT(*) FROM ((SELECT DISTINCT c_last_name, c_first_name, d_date FROM store_sales, date_dim, customer WHERE store_sales.ss_sold_date_sk = date_dim.d_date_sk AND store_sales.ss_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1213 AND 1213 + 11 AND ss_list_price BETWEEN 168 AND 197 AND c_birth_year BETWEEN 1968 AND 1974 AND ss_wholesale_cost BETWEEN 76 AND 86) EXCEPT (SELECT DISTINCT c_last_name, c_first_name, d_date FROM catalog_sales, date_dim, customer WHERE catalog_sales.cs_sold_date_sk = date_dim.d_date_sk AND catalog_sales.cs_bill_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1213 AND 1213 + 11 AND cs_list_price BETWEEN 168 AND 197 AND c_birth_year BETWEEN 1968 AND 1974 AND cs_wholesale_cost BETWEEN 76 AND 86) EXCEPT (SELECT DISTINCT c_last_name, c_first_name, d_date FROM web_sales, date_dim, customer WHERE web_sales.ws_sold_date_sk = date_dim.d_date_sk AND web_sales.ws_bill_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1213 AND 1213 + 11 AND ws_list_price BETWEEN 168 AND 197 AND c_birth_year BETWEEN 1968 AND 1974 AND ws_wholesale_cost BETWEEN 76 AND 86)) AS cool_cust