WITH prefiltered_fact_with_early_joins AS (SELECT cr_net_loss, cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status FROM catalog_returns INNER JOIN date_dim ON cr_returned_date_sk = d_date_sk AND d_year = 1998 AND d_moy = 2 INNER JOIN call_center ON cr_call_center_sk = cc_call_center_sk INNER JOIN customer ON cr_returning_customer_sk = c_customer_sk INNER JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk AND ((cd_marital_status = 'M' AND cd_education_status = 'Unknown') OR (cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree')) INNER JOIN household_demographics ON hd_demo_sk = c_current_hdemo_sk AND hd_buy_potential LIKE '1001-5000%' INNER JOIN customer_address ON ca_address_sk = c_current_addr_sk AND ca_gmt_offset = -7) SELECT cc_call_center_id AS Call_Center, cc_name AS Call_Center_Name, cc_manager AS Manager, SUM(cr_net_loss) AS Returns_Loss FROM prefiltered_fact_with_early_joins GROUP BY cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status ORDER BY SUM(cr_net_loss) DESC