WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 2), filtered_cd AS (SELECT cd_demo_sk, cd_marital_status, cd_education_status FROM customer_demographics WHERE ((cd_marital_status = 'M' AND cd_education_status = 'Unknown') OR (cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree'))), filtered_hd AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential LIKE '1001-5000%'), filtered_ca AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -7) SELECT cc.cc_call_center_id AS Call_Center, cc.cc_name AS Call_Center_Name, cc.cc_manager AS Manager, SUM(cr.cr_net_loss) AS Returns_Loss FROM filtered_date fd JOIN catalog_returns cr ON cr.cr_returned_date_sk = fd.d_date_sk JOIN call_center cc ON cr.cr_call_center_sk = cc.cc_call_center_sk JOIN customer c ON cr.cr_returning_customer_sk = c.c_customer_sk JOIN filtered_cd cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN filtered_hd hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN filtered_ca ca ON c.c_current_addr_sk = ca.ca_address_sk GROUP BY cc.cc_call_center_id, cc.cc_name, cc.cc_manager, cd.cd_marital_status, cd.cd_education_status ORDER BY SUM(cr.cr_net_loss) DESC