WITH date_filter_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy = 12), call_center_cte AS (SELECT cc_call_center_sk, cc_call_center_id, cc_name, cc_manager FROM call_center) SELECT cc_call_center_id AS Call_Center, cc_name AS Call_Center_Name, cc_manager AS Manager, SUM(cr_net_loss) AS Returns_Loss FROM catalog_returns INNER JOIN date_filter_cte ON cr_returned_date_sk = date_filter_cte.d_date_sk INNER JOIN call_center_cte ON cr_call_center_sk = call_center_cte.cc_call_center_sk INNER JOIN customer ON cr_returning_customer_sk = customer.c_customer_sk INNER JOIN customer_address ON customer.c_current_addr_sk = customer_address.ca_address_sk AND customer_address.ca_gmt_offset = -6 INNER JOIN customer_demographics ON customer.c_current_cdemo_sk = customer_demographics.cd_demo_sk AND ((customer_demographics.cd_marital_status = 'M' AND customer_demographics.cd_education_status = 'Unknown') OR (customer_demographics.cd_marital_status = 'W' AND customer_demographics.cd_education_status = 'Advanced Degree')) INNER JOIN household_demographics ON customer.c_current_hdemo_sk = household_demographics.hd_demo_sk AND household_demographics.hd_buy_potential LIKE '>10000%' GROUP BY cc_call_center_id, cc_name, cc_manager, customer_demographics.cd_marital_status, customer_demographics.cd_education_status ORDER BY SUM(cr_net_loss) DESC