WITH item_cte AS MATERIALIZED (SELECT i_item_sk, i_manufact_id, i_category FROM item WHERE (i_manufact_id BETWEEN 566 AND 765 OR i_category IN ('Children', 'Jewelry', 'Men'))), date_cte AS MATERIALIZED (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN '2002-03-06' AND CAST('2002-03-06' AS DATE) + INTERVAL '90 day'), ws_cte AS MATERIALIZED (SELECT ws_item_sk, ws_sold_date_sk, ws_ext_discount_amt, ws_wholesale_cost, ws_sales_price, ws_list_price FROM web_sales INNER JOIN date_cte ON date_cte.d_date_sk = web_sales.ws_sold_date_sk WHERE web_sales.ws_wholesale_cost BETWEEN 16 AND 36), subq_fact_cte AS MATERIALIZED (SELECT ws_item_sk, ws_ext_discount_amt FROM ws_cte WHERE ws_sales_price / ws_list_price BETWEEN 35 * 0.01 AND 50 * 0.01), avg_cte AS MATERIALIZED (SELECT ws_item_sk, 1.3 * AVG(ws_ext_discount_amt) AS threshold FROM subq_fact_cte GROUP BY ws_item_sk), explicit_joins AS MATERIALIZED (SELECT ws_cte.ws_ext_discount_amt FROM item_cte INNER JOIN ws_cte ON item_cte.i_item_sk = ws_cte.ws_item_sk INNER JOIN avg_cte ON ws_cte.ws_item_sk = avg_cte.ws_item_sk WHERE ws_cte.ws_ext_discount_amt > avg_cte.threshold) SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM explicit_joins GROUP BY () ORDER BY SUM(ws_ext_discount_amt) ASC LIMIT 100