WITH filtered_sales AS (SELECT ss_ticket_number, ss_item_sk, ss_customer_sk FROM store_sales JOIN date_dim ON ss_sold_date_sk = d_date_sk WHERE d_year BETWEEN 1998 AND 1999 AND ss_list_price BETWEEN 108 AND 122), sales_pairs AS (SELECT s1.ss_item_sk AS item1_sk, s2.ss_item_sk AS item2_sk, s1.ss_customer_sk FROM filtered_sales s1 JOIN filtered_sales s2 ON s1.ss_ticket_number = s2.ss_ticket_number AND s1.ss_item_sk < s2.ss_item_sk) SELECT item1.i_item_sk, item2.i_item_sk, COUNT(*) AS cnt FROM sales_pairs JOIN item AS item1 ON item1.i_item_sk = item1_sk JOIN item AS item2 ON item2.i_item_sk = item2_sk JOIN customer ON c_customer_sk = sales_pairs.ss_customer_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk JOIN customer_address ON ca_address_sk = c_current_addr_sk WHERE item1.i_category IN ('Electronics', 'Jewelry') AND item2.i_manager_id BETWEEN 81 AND 100 AND cd_marital_status = 'S' AND cd_education_status = 'College' GROUP BY item1.i_item_sk, item2.i_item_sk ORDER BY cnt