WITH filtered_d1 AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_year = 2000), store_returns_filtered AS (SELECT sr_ticket_number, sr_item_sk FROM store_returns INNER JOIN filtered_d1 ON sr_returned_date_sk = d_date_sk), store_sales_prefiltered AS (SELECT ss_ticket_number, ss_item_sk, ss_customer_sk FROM store_sales WHERE ss_sales_price / ss_list_price BETWEEN 76 * 0.01 AND 96 * 0.01), store_fact_join AS (SELECT ss_customer_sk, sr_item_sk FROM store_sales_prefiltered INNER JOIN store_returns_filtered ON ss_ticket_number = sr_ticket_number AND ss_item_sk = sr_item_sk), filtered_item AS (SELECT i_item_sk FROM item WHERE i_category IN ('Children', 'Home', 'Women')), filtered_d2 AS (SELECT d_date_sk, d_date FROM date_dim d2 WHERE EXISTS (SELECT 1 FROM filtered_d1 WHERE d2.d_date BETWEEN d1.d_date AND (d1.d_date + INTERVAL '90 day'))), web_sales_join AS (SELECT ss_customer_sk FROM store_fact_join INNER JOIN web_sales ON sr_item_sk = ws_item_sk AND ss_customer_sk = ws_bill_customer_sk INNER JOIN filtered_item ON sr_item_sk = i_item_sk INNER JOIN filtered_d2 ON ws_sold_date_sk = d_date_sk), customer_dimensions AS (SELECT c_customer_sk, c_first_name, c_last_name FROM web_sales_join INNER JOIN customer ON ss_customer_sk = c_customer_sk INNER JOIN customer_address ON c_current_addr_sk = ca_address_sk INNER JOIN household_demographics ON c_current_hdemo_sk = hd_demo_sk WHERE ca_state IN ('AR', 'GA', 'IN', 'KY', 'VA') AND hd_income_band_sk BETWEEN 8 AND 14 AND hd_buy_potential = '501-1000'), aggregation AS (SELECT c_customer_sk, c_first_name, c_last_name, COUNT(*) AS cnt FROM customer_dimensions GROUP BY c_customer_sk, c_first_name, c_last_name) SELECT c_customer_sk, c_first_name, c_last_name, cnt FROM aggregation ORDER BY cnt