WITH filtered_store_returns AS MATERIALIZED (SELECT sr_customer_sk, sr_store_sk, sr_reason_sk, sr_return_amt_inc_tax AS SR_RETURN_AMT_INC_TAX FROM store_returns JOIN date_dim ON sr_returned_date_sk = d_date_sk WHERE d_year = 2000 AND sr_return_amt / sr_return_quantity BETWEEN 115 AND 174), store_averages AS MATERIALIZED (SELECT sr_store_sk, AVG(SR_RETURN_AMT_INC_TAX) * 1.2 AS store_avg_threshold FROM filtered_store_returns GROUP BY sr_store_sk), customer_returns AS MATERIALIZED (SELECT sr_customer_sk AS ctr_customer_sk, sr_store_sk AS ctr_store_sk, sr_reason_sk AS ctr_reason_sk, SUM(SR_RETURN_AMT_INC_TAX) AS ctr_total_return FROM filtered_store_returns GROUP BY sr_customer_sk, sr_store_sk, sr_reason_sk) SELECT c_customer_id FROM customer_returns cr JOIN store_averages sa ON cr.ctr_store_sk = sa.sr_store_sk JOIN store ON s_store_sk = cr.ctr_store_sk JOIN customer ON c_customer_sk = cr.ctr_customer_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk WHERE cr.ctr_total_return > sa.store_avg_threshold AND cr.ctr_reason_sk BETWEEN 17 AND 20 AND s_state IN ('IA', 'KY', 'NE') AND cd_marital_status IN ('S', 'S') AND cd_education_status IN ('4 yr Degree', '4 yr Degree') AND cd_gender = 'M' AND c_birth_month = 4 AND c_birth_year BETWEEN 1987 AND 1993 ORDER BY c_customer_id LIMIT 100