WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999 AND d_moy BETWEEN 5 AND 8), filtered_items AS (SELECT i_item_sk FROM item WHERE i_category IN ('Children', 'Home', 'Women') AND i_manager_id BETWEEN 91 AND 100) SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) cnt1, cd_purchase_estimate, COUNT(*) cnt2, cd_credit_rating, COUNT(*) cnt3, cd_dep_count, COUNT(*) cnt4, cd_dep_employed_count, COUNT(*) cnt5, cd_dep_college_count, COUNT(*) cnt6 FROM customer c INNER JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk INNER JOIN customer_demographics cd ON cd.cd_demo_sk = c.c_current_cdemo_sk WHERE ca.ca_county IN ('Alameda County', 'Lexington city', 'Pender County', 'Petroleum County', 'Walworth County') AND c.c_birth_month IN (4, 5) AND cd.cd_marital_status IN ('U', 'S', 'U') AND cd.cd_education_status IN ('College', 'Secondary', 'College') AND cd.cd_gender = 'M' AND EXISTS (SELECT 1 FROM store_sales ss INNER JOIN filtered_dates fd ON ss.ss_sold_date_sk = fd.d_date_sk INNER JOIN filtered_items fi ON ss.ss_item_sk = fi.i_item_sk WHERE c.c_customer_sk = ss.ss_customer_sk AND CASE WHEN ss.ss_list_price > 0 THEN ss.ss_sales_price / ss.ss_list_price END BETWEEN 0.65 AND 0.75) AND (EXISTS (SELECT 1 FROM web_sales ws INNER JOIN filtered_dates fd ON ws.ws_sold_date_sk = fd.d_date_sk INNER JOIN filtered_items fi ON ws.ws_item_sk = fi.i_item_sk WHERE c.c_customer_sk = ws.ws_bill_customer_sk AND CASE WHEN ws.ws_list_price > 0 THEN ws.ws_sales_price / ws.ws_list_price END BETWEEN 0.65 AND 0.75) OR EXISTS (SELECT 1 FROM catalog_sales cs INNER JOIN filtered_dates fd ON cs.cs_sold_date_sk = fd.d_date_sk INNER JOIN filtered_items fi ON cs.cs_item_sk = fi.i_item_sk WHERE c.c_customer_sk = cs.cs_ship_customer_sk AND CASE WHEN cs.cs_list_price > 0 THEN cs.cs_sales_price / cs.cs_list_price END BETWEEN 0.65 AND 0.75)) GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count LIMIT 100