WITH date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998), item_cte AS (SELECT i_item_sk, i_item_id FROM item WHERE i_category = 'Sports'), cd_cte AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'F' AND cd_education_status = 'College'), customer_cte AS (SELECT c_customer_sk, c_current_addr_sk, c_birth_year FROM customer WHERE c_birth_month = 2), ca_cte AS (SELECT ca_address_sk, ca_country, ca_state, ca_county FROM customer_address WHERE ca_state IN ('AR', 'NY', 'TX')), filtered_sales AS (SELECT cs_sold_date_sk, cs_item_sk, cs_bill_cdemo_sk, cs_bill_customer_sk, cs_quantity, cs_list_price, cs_coupon_amt, cs_sales_price, cs_net_profit FROM catalog_sales WHERE cs_wholesale_cost BETWEEN 41 AND 46), join_all AS (SELECT i_item_id, ca_country, ca_state, ca_county, cs_quantity, cs_list_price, cs_coupon_amt, cs_sales_price, cs_net_profit, c_birth_year FROM filtered_sales fs INNER JOIN date_cte d ON fs.cs_sold_date_sk = d.d_date_sk INNER JOIN item_cte i ON fs.cs_item_sk = i.i_item_sk INNER JOIN cd_cte cd ON fs.cs_bill_cdemo_sk = cd.cd_demo_sk INNER JOIN customer_cte c ON fs.cs_bill_customer_sk = c.c_customer_sk INNER JOIN ca_cte ca ON c.c_current_addr_sk = ca.ca_address_sk) SELECT i_item_id, ca_country, ca_state, ca_county, avg(cast(cs_quantity as decimal(12,2))) AS agg1, avg(cast(cs_list_price as decimal(12,2))) AS agg2, avg(cast(cs_coupon_amt as decimal(12,2))) AS agg3, avg(cast(cs_sales_price as decimal(12,2))) AS agg4, avg(cast(cs_net_profit as decimal(12,2))) AS agg5, avg(cast(c_birth_year as decimal(12,2))) AS agg6 FROM join_all GROUP BY ROLLUP (i_item_id, ca_country, ca_state, ca_county) ORDER BY ca_country, ca_state, ca_county, i_item_id LIMIT 100