WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002 AND d_moy = 4), filtered_item AS (SELECT i_item_sk, i_brand_id, i_brand, i_manufact_id, i_manufact FROM item WHERE i_category = 'Jewelry'), filtered_customer AS (SELECT c_customer_sk, c_current_addr_sk FROM customer WHERE c_birth_month = 1), filtered_customer_address AS (SELECT ca_address_sk, ca_zip FROM customer_address WHERE ca_state = 'IL'), fact_join AS (SELECT ss_ext_sales_price, i_brand_id, i_brand, i_manufact_id, i_manufact, ca_zip, ss_store_sk FROM store_sales INNER JOIN filtered_date ON d_date_sk = ss_sold_date_sk INNER JOIN filtered_item ON i_item_sk = ss_item_sk INNER JOIN filtered_customer ON c_customer_sk = ss_customer_sk INNER JOIN filtered_customer_address ON ca_address_sk = c_current_addr_sk WHERE ss_wholesale_cost BETWEEN 35 AND 55), store_join AS (SELECT ss_ext_sales_price, i_brand_id, i_brand, i_manufact_id, i_manufact FROM fact_join INNER JOIN store ON s_store_sk = ss_store_sk WHERE substring(ca_zip,1,5) <> substring(s_zip,1,5)) SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ss_ext_sales_price) AS ext_price FROM store_join GROUP BY i_brand_id, i_brand, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100