WITH customer_address_cte AS (SELECT ca_address_sk, ca_zip FROM customer_address WHERE ca_state = 'IL'), customer_cte AS (SELECT c.c_customer_sk, c.c_current_addr_sk, ca.ca_zip FROM customer c INNER JOIN customer_address_cte ca ON ca.ca_address_sk = c.c_current_addr_sk WHERE c.c_birth_month = 1), date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002 AND d_moy = 4), item_cte AS (SELECT i_item_sk, i_brand_id, i_brand, i_manufact_id, i_manufact FROM item WHERE i_category = 'Jewelry'), store_cte AS (SELECT s_store_sk, s_zip FROM store), store_sales_join AS (SELECT i.i_brand_id, i.i_brand, i.i_manufact_id, i.i_manufact, ss.ss_ext_sales_price FROM store_sales ss INNER JOIN customer_cte c ON c.c_customer_sk = ss.ss_customer_sk INNER JOIN date_cte d ON d.d_date_sk = ss.ss_sold_date_sk INNER JOIN item_cte i ON i.i_item_sk = ss.ss_item_sk INNER JOIN store_cte s ON s.s_store_sk = ss.ss_store_sk WHERE ss.ss_wholesale_cost BETWEEN 35 AND 55 AND substring(c.ca_zip,1,5) <> substring(s.s_zip,1,5)) SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ss_ext_sales_price) AS ext_price FROM store_sales_join GROUP BY i_brand_id, i_brand, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100