SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ss_ext_sales_price) AS ext_price FROM date_dim INNER JOIN store_sales ON d_date_sk = ss_sold_date_sk INNER JOIN item ON ss_item_sk = i_item_sk INNER JOIN customer ON ss_customer_sk = c_customer_sk INNER JOIN customer_address ON c_current_addr_sk = ca_address_sk INNER JOIN store ON ss_store_sk = s_store_sk WHERE i_category = 'Jewelry' AND d_year = 2002 AND d_moy = 4 AND substring(ca_zip,1,5) <> substring(s_zip,1,5) AND ca_state = 'IL' AND c_birth_month = 1 AND ss_wholesale_cost BETWEEN 35 AND 55 GROUP BY i_brand, i_brand_id, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100