WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999 AND d_moy = 2), filtered_item AS (SELECT i_item_sk, i_brand_id, i_brand, i_manufact_id, i_manufact FROM item WHERE i_category = 'Shoes'), filtered_customer AS (SELECT c_customer_sk, c_current_addr_sk FROM customer WHERE c_birth_month = 2), filtered_store AS (SELECT s_store_sk, s_zip FROM store), filtered_address AS (SELECT ca_address_sk, ca_zip FROM customer_address WHERE ca_state = 'KS'), fact_prejoin AS (SELECT i.i_brand_id, i.i_brand, i.i_manufact_id, i.i_manufact, ss.ss_ext_sales_price FROM store_sales ss INNER JOIN filtered_date d ON ss.ss_sold_date_sk = d.d_date_sk INNER JOIN filtered_item i ON ss.ss_item_sk = i.i_item_sk INNER JOIN filtered_customer c ON ss.ss_customer_sk = c.c_customer_sk INNER JOIN filtered_store s ON ss.ss_store_sk = s.s_store_sk INNER JOIN filtered_address a ON c.c_current_addr_sk = a.ca_address_sk WHERE ss.ss_wholesale_cost BETWEEN 76 AND 96 AND substring(a.ca_zip,1,5) <> substring(s.s_zip,1,5)) SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ss_ext_sales_price) AS ext_price FROM fact_prejoin GROUP BY i_brand, i_brand_id, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100