WITH joined AS (SELECT ss.i_item_id, s.s_state, ss.ss_quantity, ss.ss_list_price, ss.ss_coupon_amt, ss.ss_sales_price FROM store_sales ss INNER JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk AND d.d_year = 2002 INNER JOIN store s ON ss.ss_store_sk = s.s_store_sk AND s.s_state = 'IA' INNER JOIN item i ON ss.ss_item_sk = i.i_item_sk AND i.i_category = 'Sports' INNER JOIN customer_demographics cd ON ss.ss_cdemo_sk = cd.cd_demo_sk AND cd.cd_gender = 'F' AND cd.cd_marital_status = 'M' AND cd.cd_education_status = 'Primary'), aggregated AS (SELECT i_item_id, s_state, GROUPING(s_state) AS g_state, AVG(ss_quantity) AS agg1, AVG(ss_list_price) AS agg2, AVG(ss_coupon_amt) AS agg3, AVG(ss_sales_price) AS agg4 FROM joined GROUP BY ROLLUP (i_item_id, s_state)) SELECT i_item_id, s_state, g_state, agg1, agg2, agg3, agg4 FROM aggregated ORDER BY i_item_id, s_state LIMIT 100