WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999), filtered_items AS (SELECT i_item_sk FROM item WHERE i_manager_id BETWEEN 42 AND 51), joined_fact_dims AS (SELECT wr_returning_customer_sk, ca_state, wr_reason_sk, wr_return_amt FROM web_returns INNER JOIN filtered_dates ON wr_returned_date_sk = d_date_sk INNER JOIN customer_address ON wr_returning_addr_sk = ca_address_sk INNER JOIN filtered_items ON wr_item_sk = i_item_sk WHERE wr_return_amt / wr_return_quantity BETWEEN 193 AND 222), aggregated_cte AS (SELECT wr_returning_customer_sk AS ctr_customer_sk, ca_state AS ctr_state, wr_reason_sk AS ctr_reason_sk, SUM(wr_return_amt) AS ctr_total_return FROM joined_fact_dims GROUP BY wr_returning_customer_sk, ca_state, wr_reason_sk), state_avg_cte AS (SELECT ctr_state, AVG(ctr_total_return) * 1.2 AS state_avg_threshold FROM aggregated_cte GROUP BY ctr_state)
SELECT c_customer_id, c_salutation, c_first_name, c_last_name, c_preferred_cust_flag, c_birth_day, c_birth_month, c_birth_year, c_birth_country, c_login, c_email_address, c_last_review_date_sk, ctr_total_return FROM aggregated_cte AS ctr1 INNER JOIN customer ON ctr1.ctr_customer_sk = c_customer_sk INNER JOIN customer_address ON ca_address_sk = c_current_addr_sk INNER JOIN state_avg_cte ON ctr1.ctr_state = state_avg_cte.ctr_state WHERE ctr1.ctr_reason_sk IN (22, 23) AND ca_state IN ('CA', 'MI', 'SD', 'VA') AND c_birth_year BETWEEN 1945 AND 1951 AND ctr1.ctr_total_return > state_avg_cte.state_avg_threshold ORDER BY c_customer_id, c_salutation, c_first_name, c_last_name, c_preferred_cust_flag, c_birth_day, c_birth_month, c_birth_year, c_birth_country, c_login, c_email_address, c_last_review_date_sk, ctr_total_return LIMIT 100