WITH filtered_date AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_month_seq BETWEEN 1207 AND 1207 + 11), filtered_customer AS (SELECT c_customer_sk, c_last_name, c_first_name FROM customer WHERE c_birth_month IN (2, 3, 4, 10)), store_channel AS (SELECT DISTINCT filtered_customer.c_last_name, filtered_customer.c_first_name, filtered_date.d_date FROM store_sales INNER JOIN filtered_date ON store_sales.ss_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_customer ON store_sales.ss_customer_sk = filtered_customer.c_customer_sk WHERE store_sales.ss_list_price BETWEEN 241 AND 300 AND store_sales.ss_wholesale_cost BETWEEN 76 AND 96), catalog_channel AS (SELECT DISTINCT filtered_customer.c_last_name, filtered_customer.c_first_name, filtered_date.d_date FROM catalog_sales INNER JOIN filtered_date ON catalog_sales.cs_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_customer ON catalog_sales.cs_bill_customer_sk = filtered_customer.c_customer_sk WHERE catalog_sales.cs_list_price BETWEEN 241 AND 300 AND catalog_sales.cs_wholesale_cost BETWEEN 76 AND 96), web_channel AS (SELECT DISTINCT filtered_customer.c_last_name, filtered_customer.c_first_name, filtered_date.d_date FROM web_sales INNER JOIN filtered_date ON web_sales.ws_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_customer ON web_sales.ws_bill_customer_sk = filtered_customer.c_customer_sk WHERE web_sales.ws_list_price BETWEEN 241 AND 300 AND web_sales.ws_wholesale_cost BETWEEN 76 AND 96) SELECT COUNT(*) FROM (SELECT c_last_name, c_first_name, d_date FROM store_channel INTERSECT SELECT c_last_name, c_first_name, d_date FROM catalog_channel INTERSECT SELECT c_last_name, c_first_name, d_date FROM web_channel) AS hot_cust LIMIT 100