WITH date_cte AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('2002-02-19' AS DATE) - INTERVAL '30 DAY') AND (CAST('2002-02-19' AS DATE) + INTERVAL '30 DAY')), item_cte AS (SELECT i_item_sk, i_item_id FROM item WHERE i_category = 'Jewelry' AND i_manager_id BETWEEN 61 AND 100), returns_cte AS (SELECT cr_order_number, cr_item_sk, cr_refunded_cash FROM catalog_returns WHERE cr_reason_sk = 33), warehouse_cte AS (SELECT w_warehouse_sk, w_state FROM warehouse), fact_join AS (SELECT w.w_state, i.i_item_id, d.d_date, cs.cs_sales_price, r.cr_refunded_cash FROM catalog_sales cs INNER JOIN date_cte d ON cs.cs_sold_date_sk = d.d_date_sk INNER JOIN item_cte i ON i.i_item_sk = cs.cs_item_sk INNER JOIN warehouse_cte w ON cs.cs_warehouse_sk = w.w_warehouse_sk LEFT JOIN returns_cte r ON cs.cs_order_number = r.cr_order_number AND cs.cs_item_sk = r.cr_item_sk WHERE cs.cs_wholesale_cost BETWEEN 42 AND 61 AND r.cr_order_number IS NOT NULL) SELECT w_state, i_item_id, SUM(CASE WHEN d_date < CAST('2002-02-19' AS DATE) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_before, SUM(CASE WHEN d_date >= CAST('2002-02-19' AS DATE) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_after FROM fact_join GROUP BY w_state, i_item_id ORDER BY w_state, i_item_id LIMIT 100