WITH date_cte AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('2002-02-19' AS DATE) - INTERVAL '30 DAY') AND (CAST('2002-02-19' AS DATE) + INTERVAL '30 DAY')) SELECT w_state, i_item_id, SUM(CASE WHEN d_date < CAST('2002-02-19' AS DATE) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_before, SUM(CASE WHEN d_date >= CAST('2002-02-19' AS DATE) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_after FROM catalog_sales JOIN date_cte ON cs_sold_date_sk = d_date_sk JOIN item ON i_item_sk = cs_item_sk JOIN warehouse ON cs_warehouse_sk = w_warehouse_sk LEFT JOIN catalog_returns ON cs_order_number = cr_order_number AND cs_item_sk = cr_item_sk AND cr_reason_sk = 33 WHERE i_category = 'Jewelry' AND i_manager_id BETWEEN 61 AND 100 AND cs_wholesale_cost BETWEEN 42 AND 61 GROUP BY w_state, i_item_id ORDER BY w_state, i_item_id LIMIT 100