WITH date_filter AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('2002-02-20' AS DATE) - INTERVAL '30 DAY') AND (CAST('2002-02-20' AS DATE) + INTERVAL '30 DAY')), fact_agg_by_keys AS (SELECT cs_item_sk, cs_warehouse_sk, d_date, SUM(CASE WHEN (CAST(d_date AS DATE) < CAST('2002-02-20' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash,0) ELSE 0 END) AS sales_before, SUM(CASE WHEN (CAST(d_date AS DATE) >= CAST('2002-02-20' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash,0) ELSE 0 END) AS sales_after FROM catalog_sales INNER JOIN date_filter ON (cs_sold_date_sk = date_filter.d_date_sk) LEFT OUTER JOIN catalog_returns ON (cs_order_number = cr_order_number AND cs_item_sk = cr_item_sk AND cr_reason_sk = 40) WHERE cs_wholesale_cost BETWEEN 68 AND 87 GROUP BY cs_item_sk, cs_warehouse_sk, d_date), dimension_join AS (SELECT w.w_state, i.i_item_id, SUM(f.sales_before) AS sales_before, SUM(f.sales_after) AS sales_after FROM fact_agg_by_keys f INNER JOIN item i ON (f.cs_item_sk = i.i_item_sk) INNER JOIN warehouse w ON (f.cs_warehouse_sk = w.w_warehouse_sk) WHERE i.i_category = 'Shoes' AND i.i_manager_id BETWEEN 42 AND 81 GROUP BY w.w_state, i.i_item_id) SELECT w_state, i_item_id, sales_before, sales_after FROM dimension_join ORDER BY w_state, i_item_id LIMIT 100