WITH date_cte AS MATERIALIZED (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('2002-02-20' AS DATE) - INTERVAL '30 DAY') AND (CAST('2002-02-20' AS DATE) + INTERVAL '30 DAY')), item_cte AS MATERIALIZED (SELECT i_item_sk, i_item_id FROM item WHERE i_category = 'Shoes' AND i_manager_id BETWEEN 42 AND 81), fact_cte AS (SELECT cs.cs_item_sk, cs.cs_warehouse_sk, cs.cs_order_number, cs.cs_sales_price, dc.d_date, ic.i_item_id FROM catalog_sales cs INNER JOIN date_cte dc ON cs.cs_sold_date_sk = dc.d_date_sk INNER JOIN item_cte ic ON cs.cs_item_sk = ic.i_item_sk WHERE cs.cs_wholesale_cost BETWEEN 68 AND 87), final_join AS (SELECT w.w_state, fj.i_item_id, fj.d_date, fj.cs_sales_price, cr.cr_refunded_cash FROM fact_cte fj INNER JOIN warehouse w ON fj.cs_warehouse_sk = w.w_warehouse_sk LEFT OUTER JOIN catalog_returns cr ON fj.cs_order_number = cr.cr_order_number AND fj.cs_item_sk = cr.cr_item_sk AND cr.cr_reason_sk = 40), aggregation AS (SELECT w_state, i_item_id, SUM(CASE WHEN (CAST(d_date AS DATE) < CAST('2002-02-20' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_before, SUM(CASE WHEN (CAST(d_date AS DATE) >= CAST('2002-02-20' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_after FROM final_join GROUP BY w_state, i_item_id) SELECT w_state, i_item_id, sales_before, sales_after FROM aggregation ORDER BY w_state, i_item_id LIMIT 100