WITH filtered_dimensions AS (SELECT i.i_item_sk, df.d_date_sk, cf.c_customer_sk, cf.c_current_addr_sk FROM item i CROSS JOIN (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 1) df CROSS JOIN (SELECT c_customer_sk, c_current_addr_sk FROM customer WHERE c_birth_year BETWEEN 1928 AND 1941) cf WHERE i.i_category = 'Electronics' AND i.i_class = 'personal'), catalog_facts AS (SELECT fd.c_customer_sk, fd.c_current_addr_sk FROM filtered_dimensions fd INNER JOIN catalog_sales cs ON fd.i_item_sk = cs.cs_item_sk AND fd.d_date_sk = cs.cs_sold_date_sk AND fd.c_customer_sk = cs.cs_bill_customer_sk WHERE cs.cs_wholesale_cost BETWEEN 35 AND 65), web_facts AS (SELECT fd.c_customer_sk, fd.c_current_addr_sk FROM filtered_dimensions fd INNER JOIN web_sales ws ON fd.i_item_sk = ws.ws_item_sk AND fd.d_date_sk = ws.ws_sold_date_sk AND fd.c_customer_sk = ws.ws_bill_customer_sk WHERE ws.ws_wholesale_cost BETWEEN 35 AND 65), union_all AS (SELECT c_customer_sk, c_current_addr_sk FROM catalog_facts UNION ALL SELECT c_customer_sk, c_current_addr_sk FROM web_facts), my_customers AS (SELECT DISTINCT c_customer_sk, c_current_addr_sk FROM union_all), my_revenue AS (SELECT c_customer_sk, SUM(ss_ext_sales_price) AS revenue FROM my_customers, store_sales, customer_address, store, date_dim WHERE c_current_addr_sk = ca_address_sk AND ca_county = s_county AND ca_state = s_state AND ss_sold_date_sk = d_date_sk AND c_customer_sk = ss_customer_sk AND ss_wholesale_cost BETWEEN 35 AND 65 AND s_state IN ('AR', 'CO', 'IA', 'IL', 'KY', 'NC', 'NM', 'NY', 'PA', 'TX') AND d_month_seq BETWEEN (SELECT DISTINCT d_month_seq + 1 FROM date_dim WHERE d_year = 1998 AND d_moy = 1) AND (SELECT DISTINCT d_month_seq + 3 FROM date_dim WHERE d_year = 1998 AND d_moy = 1) GROUP BY c_customer_sk), segments AS (SELECT CAST((revenue / 50) AS INT) AS segment FROM my_revenue) SELECT segment, COUNT(*) AS num_customers, segment * 50 AS segment_base FROM segments GROUP BY segment ORDER BY segment, num_customers LIMIT 100