WITH store_cte AS (SELECT s_store_sk, s_store_name FROM store WHERE s_state IN ('IA','IL','NC')), item_cte AS (SELECT i_item_sk, i_item_desc, i_current_price, i_wholesale_cost, i_brand FROM item WHERE i_manager_id BETWEEN 80 AND 84), date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_month_seq BETWEEN 1195 AND 1195+11), sales_agg AS (SELECT ss_store_sk, ss_item_sk, SUM(ss_sales_price) AS revenue FROM store_sales JOIN date_cte ON ss_sold_date_sk = d_date_sk WHERE ss_sales_price / ss_list_price BETWEEN 0.38 AND 0.48 GROUP BY ss_store_sk, ss_item_sk), store_avg AS (SELECT ss_store_sk, AVG(revenue) AS ave FROM sales_agg GROUP BY ss_store_sk) SELECT s.s_store_name, i.i_item_desc, g.revenue, i.i_current_price, i.i_wholesale_cost, i.i_brand FROM sales_agg g JOIN store_avg a ON g.ss_store_sk = a.ss_store_sk JOIN store_cte s ON s.s_store_sk = g.ss_store_sk JOIN item_cte i ON i.i_item_sk = g.ss_item_sk WHERE g.revenue <= 0.1 * a.ave ORDER BY s.s_store_name, i.i_item_desc LIMIT 100