WITH filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_state IN ('GA', 'KY', 'SD')), filtered_demo AS (SELECT cd_demo_sk, cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating FROM customer_demographics WHERE cd_marital_status IN ('D', 'W', 'U') AND cd_education_status IN ('Primary', 'College')), filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999 AND d_moy BETWEEN 3 AND 5), customer_base AS (SELECT c.c_customer_sk, fd.cd_gender, fd.cd_marital_status, fd.cd_education_status, fd.cd_purchase_estimate, fd.cd_credit_rating FROM customer c INNER JOIN filtered_address fa ON c.c_current_addr_sk = fa.ca_address_sk INNER JOIN filtered_demo fd ON c.c_current_cdemo_sk = fd.cd_demo_sk), store_sales_filtered AS (SELECT ss_customer_sk FROM store_sales INNER JOIN filtered_date fd ON ss_sold_date_sk = fd.d_date_sk WHERE ss_list_price BETWEEN 52 AND 141), store_join AS (SELECT cb.* FROM customer_base cb INNER JOIN store_sales_filtered ssf ON cb.c_customer_sk = ssf.ss_customer_sk), web_sales_filtered AS (SELECT ws_bill_customer_sk FROM web_sales INNER JOIN filtered_date fd ON ws_sold_date_sk = fd.d_date_sk WHERE ws_list_price BETWEEN 52 AND 141), web_anti_join AS (SELECT sj.* FROM store_join sj LEFT JOIN web_sales_filtered wsf ON sj.c_customer_sk = wsf.ws_bill_customer_sk WHERE wsf.ws_bill_customer_sk IS NULL), catalog_sales_filtered AS (SELECT cs_ship_customer_sk FROM catalog_sales INNER JOIN filtered_date fd ON cs_sold_date_sk = fd.d_date_sk WHERE cs_list_price BETWEEN 52 AND 141), catalog_anti_join AS (SELECT waj.* FROM web_anti_join waj LEFT JOIN catalog_sales_filtered csf ON waj.c_customer_sk = csf.cs_ship_customer_sk WHERE csf.cs_ship_customer_sk IS NULL) SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3 FROM catalog_anti_join GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating LIMIT 100