WITH filtered_date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999 AND d_moy BETWEEN 3 AND 5), store_customers_cte AS (SELECT DISTINCT ss_customer_sk FROM store_sales JOIN filtered_date_cte ON ss_sold_date_sk = d_date_sk WHERE ss_list_price BETWEEN 52 AND 141), web_customers_cte AS (SELECT DISTINCT ws_bill_customer_sk FROM web_sales JOIN filtered_date_cte ON ws_sold_date_sk = d_date_sk WHERE ws_list_price BETWEEN 52 AND 141), catalog_customers_cte AS (SELECT DISTINCT cs_ship_customer_sk FROM catalog_sales JOIN filtered_date_cte ON cs_sold_date_sk = d_date_sk WHERE cs_list_price BETWEEN 52 AND 141), customer_base_cte AS (SELECT c.c_customer_sk, cd.cd_gender, cd.cd_marital_status, cd.cd_education_status, cd.cd_purchase_estimate, cd.cd_credit_rating FROM customer c JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk WHERE ca.ca_state IN ('GA','KY','SD') AND cd.cd_marital_status IN ('D','W','U') AND cd.cd_education_status IN ('Primary','College')) SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3 FROM customer_base_cte cb JOIN store_customers_cte sc ON cb.c_customer_sk = sc.ss_customer_sk LEFT JOIN web_customers_cte wc ON cb.c_customer_sk = wc.ws_bill_customer_sk LEFT JOIN catalog_customers_cte cc ON cb.c_customer_sk = cc.cs_ship_customer_sk WHERE wc.ws_bill_customer_sk IS NULL AND cc.cs_ship_customer_sk IS NULL GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating LIMIT 100