WITH filtered_dims AS (SELECT d1.d_date_sk AS d1_date_sk, d1.d_date AS d1_date, d1.d_week_seq AS d1_week_seq, d2.d_date_sk AS d2_date_sk, d2.d_week_seq AS d2_week_seq, d3.d_date_sk AS d3_date_sk, d3.d_date AS d3_date, i.i_item_sk, i.i_item_desc, cd.cd_demo_sk, hd.hd_demo_sk FROM date_dim d1, date_dim d2, date_dim d3, item i, customer_demographics cd, household_demographics hd WHERE d1.d_week_seq = d2.d_week_seq AND d1.d_year = 1998 AND hd.hd_buy_potential = '>10000' AND cd.cd_marital_status = 'U' AND cd.cd_dep_count BETWEEN 9 AND 11 AND i.i_category IN ('Children', 'Jewelry', 'Men')), filtered_fact AS (SELECT cs.cs_item_sk, cs.cs_quantity, cs.cs_wholesale_cost, cs.cs_ship_date_sk, cs.cs_promo_sk, cs.cs_order_number, fd.d1_date, fd.d1_week_seq, fd.d2_date_sk, fd.d2_week_seq, fd.d3_date_sk, fd.d3_date, fd.i_item_desc FROM catalog_sales cs INNER JOIN filtered_dims fd ON cs.cs_sold_date_sk = fd.d1_date_sk AND cs.cs_item_sk = fd.i_item_sk AND cs.cs_bill_cdemo_sk = fd.cd_demo_sk AND cs.cs_bill_hdemo_sk = fd.hd_demo_sk WHERE cs.cs_wholesale_cost BETWEEN 35 AND 55), joined_with_inventory AS (SELECT ff.i_item_desc, ff.cs_item_sk, ff.cs_promo_sk, ff.cs_order_number, ff.d1_week_seq, inv.inv_warehouse_sk AS w_warehouse_sk FROM filtered_fact ff INNER JOIN inventory inv ON inv.inv_item_sk = ff.cs_item_sk AND inv.inv_date_sk = ff.d2_date_sk WHERE inv.inv_quantity_on_hand < ff.cs_quantity AND ff.d3_date > ff.d1_date + INTERVAL '3 day') SELECT jwi.i_item_desc, w.w_warehouse_name, jwi.d1_week_seq AS d_week_seq, SUM(CASE WHEN p.p_promo_sk IS NULL THEN 1 ELSE 0 END) AS no_promo, SUM(CASE WHEN p.p_promo_sk IS NOT NULL THEN 1 ELSE 0 END) AS promo, COUNT(*) AS total_cnt FROM joined_with_inventory jwi INNER JOIN warehouse w ON w.w_warehouse_sk = jwi.w_warehouse_sk LEFT OUTER JOIN promotion p ON jwi.cs_promo_sk = p.p_promo_sk LEFT OUTER JOIN catalog_returns cr ON cr.cr_item_sk = jwi.cs_item_sk AND cr.cr_order_number = jwi.cs_order_number GROUP BY jwi.i_item_desc, w.w_warehouse_name, jwi.d1_week_seq ORDER BY total_cnt DESC, jwi.i_item_desc, w.w_warehouse_name, jwi.d1_week_seq LIMIT 100