WITH filtered_item AS (SELECT i_item_sk, i_brand_id, i_class_id, i_category_id, i_manufact_id FROM item WHERE i_category = 'Children'), filtered_date AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_year IN (1998, 1999)), all_sales_details AS (SELECT d_year, i_brand_id, i_class_id, i_category_id, i_manufact_id, sales_cnt, sales_amt FROM (SELECT d.d_year, i.i_brand_id, i.i_class_id, i.i_category_id, i.i_manufact_id, cs.cs_quantity - COALESCE(cr.cr_return_quantity, 0) AS sales_cnt, cs.cs_ext_sales_price - COALESCE(cr.cr_return_amount, 0.0) AS sales_amt FROM catalog_sales cs JOIN filtered_item i ON cs.cs_item_sk = i.i_item_sk JOIN filtered_date d ON cs.cs_sold_date_sk = d.d_date_sk JOIN catalog_returns cr ON cs.cs_order_number = cr.cr_order_number AND cs.cs_item_sk = cr.cr_item_sk WHERE cs.cs_sales_price / cs.cs_list_price BETWEEN 69 * 0.01 AND 89 * 0.01 AND cr.cr_reason_sk IN (8, 18, 20, 23, 41) UNION ALL SELECT d.d_year, i.i_brand_id, i.i_class_id, i.i_category_id, i.i_manufact_id, ss.ss_quantity - COALESCE(sr.sr_return_quantity, 0) AS sales_cnt, ss.ss_ext_sales_price - COALESCE(sr.sr_return_amt, 0.0) AS sales_amt FROM store_sales ss JOIN filtered_item i ON ss.ss_item_sk = i.i_item_sk JOIN filtered_date d ON ss.ss_sold_date_sk = d.d_date_sk JOIN store_returns sr ON ss.ss_ticket_number = sr.sr_ticket_number AND ss.ss_item_sk = sr.sr_item_sk WHERE ss.ss_sales_price / ss.ss_list_price BETWEEN 69 * 0.01 AND 89 * 0.01 AND sr.sr_reason_sk IN (8, 18, 20, 23, 41) UNION ALL SELECT d.d_year, i.i_brand_id, i.i_class_id, i.i_category_id, i.i_manufact_id, ws.ws_quantity - COALESCE(wr.wr_return_quantity, 0) AS sales_cnt, ws.ws_ext_sales_price - COALESCE(wr.wr_return_amt, 0.0) AS sales_amt FROM web_sales ws JOIN filtered_item i ON ws.ws_item_sk = i.i_item_sk JOIN filtered_date d ON ws.ws_sold_date_sk = d.d_date_sk JOIN web_returns wr ON ws.ws_order_number = wr.wr_order_number AND ws.ws_item_sk = wr.wr_item_sk WHERE ws.ws_sales_price / ws.ws_list_price BETWEEN 69 * 0.01 AND 89 * 0.01 AND wr.wr_reason_sk IN (8, 18, 20, 23, 41)) AS t), aggregated_sales AS (SELECT i_brand_id, i_class_id, i_category_id, i_manufact_id, SUM(CASE WHEN d_year = 1998 THEN sales_cnt ELSE 0 END) AS prev_yr_cnt, SUM(CASE WHEN d_year = 1999 THEN sales_cnt ELSE 0 END) AS curr_yr_cnt, SUM(CASE WHEN d_year = 1998 THEN sales_amt ELSE 0 END) AS prev_yr_amt, SUM(CASE WHEN d_year = 1999 THEN sales_amt ELSE 0 END) AS curr_yr_amt FROM all_sales_details GROUP BY i_brand_id, i_class_id, i_category_id, i_manufact_id HAVING SUM(CASE WHEN d_year = 1998 THEN sales_cnt ELSE 0 END) > 0 AND CAST(SUM(CASE WHEN d_year = 1999 THEN sales_cnt ELSE 0 END) AS DECIMAL(17,2)) / CAST(SUM(CASE WHEN d_year = 1998 THEN sales_cnt ELSE 0 END) AS DECIMAL(17,2)) < 0.9) SELECT 1998 AS prev_year, 1999 AS year, i_brand_id, i_class_id, i_category_id, i_manufact_id, prev_yr_cnt AS prev_yr_cnt, curr_yr_cnt AS curr_yr_cnt, curr_yr_cnt - prev_yr_cnt AS sales_cnt_diff, curr_yr_amt - prev_yr_amt AS sales_amt_diff FROM aggregated_sales ORDER BY sales_cnt_diff, sales_amt_diff LIMIT 100