WITH date_filter_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002), returning_addr_cte AS (SELECT ca_address_sk, ca_state FROM customer_address), fact_agg_cte AS (SELECT cr.cr_returning_customer_sk AS ctr_customer_sk, ra.ca_state AS ctr_state, SUM(cr.cr_return_amt_inc_tax) AS ctr_total_return FROM catalog_returns cr INNER JOIN date_filter_cte df ON cr.cr_returned_date_sk = df.d_date_sk INNER JOIN returning_addr_cte ra ON cr.cr_returning_addr_sk = ra.ca_address_sk GROUP BY cr.cr_returning_customer_sk, ra.ca_state), state_avg_cte AS (SELECT ctr_state, AVG(ctr_total_return) * 1.2 AS state_avg_threshold FROM fact_agg_cte GROUP BY ctr_state)
SELECT c.c_customer_id, c.c_salutation, c.c_first_name, c.c_last_name, ca.ca_street_number, ca.ca_street_name, ca.ca_street_type, ca.ca_suite_number, ca.ca_city, ca.ca_county, ca.ca_state, ca.ca_zip, ca.ca_country, ca.ca_gmt_offset, ca.ca_location_type, f.ctr_total_return FROM fact_agg_cte f INNER JOIN state_avg_cte s ON f.ctr_state = s.ctr_state INNER JOIN customer c ON f.ctr_customer_sk = c.c_customer_sk INNER JOIN customer_address ca ON ca.ca_address_sk = c.c_current_addr_sk WHERE ca.ca_state = 'OH' AND f.ctr_total_return > s.state_avg_threshold ORDER BY c.c_customer_id, c.c_salutation, c.c_first_name, c.c_last_name, ca.ca_street_number, ca.ca_street_name, ca.ca_street_type, ca.ca_suite_number, ca.ca_city, ca.ca_county, ca.ca_state, ca.ca_zip, ca.ca_country, ca.ca_gmt_offset, ca.ca_location_type, f.ctr_total_return LIMIT 100