WITH filtered_customer_address AS (SELECT * FROM customer_address WHERE ca_city = 'Jackson'), filtered_income_band AS (SELECT * FROM income_band WHERE ib_lower_bound >= 23567 AND ib_upper_bound <= 73567), filtered_household_demographics AS (SELECT hd.* FROM household_demographics hd INNER JOIN filtered_income_band fib ON hd.hd_income_band_sk = fib.ib_income_band_sk), filtered_customer_demographics AS (SELECT * FROM customer_demographics), customer_join AS (SELECT c.* FROM customer c INNER JOIN filtered_customer_address fca ON c.c_current_addr_sk = fca.ca_address_sk INNER JOIN filtered_household_demographics fhd ON c.c_current_hdemo_sk = fhd.hd_demo_sk INNER JOIN filtered_customer_demographics fcd ON c.c_current_cdemo_sk = fcd.cd_demo_sk), store_returns_join AS (SELECT cj.c_customer_id, COALESCE(cj.c_last_name, '') || ', ' || COALESCE(cj.c_first_name, '') AS customername FROM customer_join cj INNER JOIN store_returns sr ON sr.sr_cdemo_sk = cj.c_current_cdemo_sk) SELECT c_customer_id AS customer_id, customername FROM store_returns_join ORDER BY c_customer_id LIMIT 100