WITH filtered_customer_address AS (SELECT ca_address_sk, ca_location_type, ca_street_type, ca_county, ca_city, ca_state, ca_zip, ca_country, ca_gmt_offset, ca_street_name FROM customer_address WHERE ca_city = 'Mount Vernon'), filtered_income_band AS (SELECT ib_income_band_sk, ib_lower_bound, ib_upper_bound FROM income_band WHERE ib_lower_bound >= 40374 AND ib_upper_bound <= 90374), prefetched_dims AS (SELECT fca.ca_address_sk, hd.hd_demo_sk, hd.hd_income_band_sk FROM filtered_customer_address fca CROSS JOIN filtered_income_band fib INNER JOIN household_demographics hd ON hd.hd_income_band_sk = fib.ib_income_band_sk) SELECT c_customer_id AS customer_id, COALESCE(c_last_name,'') || ', ' || COALESCE(c_first_name,'') AS customername FROM customer INNER JOIN prefetched_dims ON customer.c_current_addr_sk = prefetched_dims.ca_address_sk AND customer.c_current_hdemo_sk = prefetched_dims.hd_demo_sk INNER JOIN customer_demographics ON customer_demographics.cd_demo_sk = customer.c_current_cdemo_sk INNER JOIN store_returns ON store_returns.sr_cdemo_sk = customer_demographics.cd_demo_sk ORDER BY c_customer_id LIMIT 100