WITH fact_with_cdemo AS (SELECT store_returns.*, customer_demographics.* FROM store_returns INNER JOIN customer_demographics ON store_returns.sr_cdemo_sk = customer_demographics.cd_demo_sk) SELECT customer.c_customer_id AS customer_id, COALESCE(customer.c_last_name,'') || ', ' || COALESCE(customer.c_first_name,'') AS customername FROM fact_with_cdemo INNER JOIN customer ON customer.c_current_cdemo_sk = fact_with_cdemo.cd_demo_sk INNER JOIN customer_address ON customer.c_current_addr_sk = customer_address.ca_address_sk INNER JOIN household_demographics ON household_demographics.hd_demo_sk = customer.c_current_hdemo_sk INNER JOIN income_band ON income_band.ib_income_band_sk = household_demographics.hd_income_band_sk WHERE customer_address.ca_city = 'Mount Vernon' AND income_band.ib_lower_bound >= 40374 AND income_band.ib_upper_bound <= 90374 ORDER BY customer.c_customer_id LIMIT 100