WITH date_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000) SELECT substring(r_reason_desc, 1, 20), avg(ws_quantity), avg(wr_refunded_cash), avg(wr_fee) FROM web_sales INNER JOIN date_cte ON web_sales.ws_sold_date_sk = date_cte.d_date_sk INNER JOIN web_returns ON web_sales.ws_item_sk = web_returns.wr_item_sk AND web_sales.ws_order_number = web_returns.wr_order_number INNER JOIN web_page ON web_sales.ws_web_page_sk = web_page.wp_web_page_sk INNER JOIN customer_demographics cd1 ON web_returns.wr_refunded_cdemo_sk = cd1.cd_demo_sk INNER JOIN customer_demographics cd2 ON web_returns.wr_returning_cdemo_sk = cd2.cd_demo_sk INNER JOIN customer_address ON web_returns.wr_refunded_addr_sk = customer_address.ca_address_sk INNER JOIN reason ON web_returns.wr_reason_sk = reason.r_reason_sk WHERE ((cd1.cd_marital_status = 'S' AND cd1.cd_marital_status = cd2.cd_marital_status AND cd1.cd_education_status = 'College' AND cd1.cd_education_status = cd2.cd_education_status AND web_sales.ws_sales_price BETWEEN 100.00 AND 150.00) OR (cd1.cd_marital_status = 'W' AND cd1.cd_marital_status = cd2.cd_marital_status AND cd1.cd_education_status = 'Unknown' AND cd1.cd_education_status = cd2.cd_education_status AND web_sales.ws_sales_price BETWEEN 50.00 AND 100.00) OR (cd1.cd_marital_status = 'M' AND cd1.cd_marital_status = cd2.cd_marital_status AND cd1.cd_education_status = 'Secondary' AND cd1.cd_education_status = cd2.cd_education_status AND web_sales.ws_sales_price BETWEEN 150.00 AND 200.00)) AND ((customer_address.ca_country = 'United States' AND customer_address.ca_state IN ('NC', 'PA', 'TX') AND web_sales.ws_net_profit BETWEEN 100 AND 200) OR (customer_address.ca_country = 'United States' AND customer_address.ca_state IN ('AR', 'CO', 'NY') AND web_sales.ws_net_profit BETWEEN 150 AND 300) OR (customer_address.ca_country = 'United States' AND customer_address.ca_state IN ('IA', 'IL', 'TX') AND web_sales.ws_net_profit BETWEEN 50 AND 250)) GROUP BY r_reason_desc ORDER BY substring(r_reason_desc, 1, 20), avg(ws_quantity), avg(wr_refunded_cash), avg(wr_fee) LIMIT 100