WITH date_filter AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002), customer_demographics_filter AS (SELECT cd_demo_sk, cd_marital_status, cd_education_status FROM customer_demographics WHERE (cd_marital_status = 'S' AND cd_education_status = 'Primary') OR (cd_marital_status = 'M' AND cd_education_status = 'Secondary') OR (cd_marital_status = 'U' AND cd_education_status = '2 yr Degree')), customer_address_filter AS (SELECT ca_address_sk, ca_state FROM customer_address WHERE ca_country = 'United States' AND ca_state IN ('KY','NC','SD','AR','GA','IN','KS','OH','VA')), reason_filter AS (SELECT r_reason_sk, r_reason_desc FROM reason), web_sales_join AS (SELECT ws_item_sk, ws_order_number, ws_web_page_sk, ws_quantity, ws_sales_price, ws_net_profit FROM web_sales INNER JOIN date_filter ON ws_sold_date_sk = date_filter.d_date_sk WHERE ((ws_sales_price BETWEEN 100.00 AND 150.00) OR (ws_sales_price BETWEEN 50.00 AND 100.00) OR (ws_sales_price BETWEEN 150.00 AND 200.00))), web_returns_join AS (SELECT r_reason_desc, ws_quantity, wr_refunded_cash, wr_fee FROM web_returns INNER JOIN web_sales_join ON web_returns.wr_item_sk = web_sales_join.ws_item_sk AND web_returns.wr_order_number = web_sales_join.ws_order_number INNER JOIN customer_demographics_filter cd1 ON cd1.cd_demo_sk = web_returns.wr_refunded_cdemo_sk INNER JOIN customer_demographics cd2 ON cd2.cd_demo_sk = web_returns.wr_returning_cdemo_sk AND cd1.cd_marital_status = cd2.cd_marital_status AND cd1.cd_education_status = cd2.cd_education_status INNER JOIN customer_address_filter ON customer_address_filter.ca_address_sk = web_returns.wr_refunded_addr_sk INNER JOIN reason_filter ON reason_filter.r_reason_sk = web_returns.wr_reason_sk INNER JOIN web_page ON web_sales_join.ws_web_page_sk = web_page.wp_web_page_sk WHERE ((cd1.cd_marital_status = 'S' AND cd1.cd_education_status = 'Primary' AND web_sales_join.ws_sales_price BETWEEN 100.00 AND 150.00) OR (cd1.cd_marital_status = 'M' AND cd1.cd_education_status = 'Secondary' AND web_sales_join.ws_sales_price BETWEEN 50.00 AND 100.00) OR (cd1.cd_marital_status = 'U' AND cd1.cd_education_status = '2 yr Degree' AND web_sales_join.ws_sales_price BETWEEN 150.00 AND 200.00)) AND ((customer_address_filter.ca_state IN ('KY','NC','SD') AND web_sales_join.ws_net_profit BETWEEN 100 AND 200) OR (customer_address_filter.ca_state IN ('AR','GA','IN') AND web_sales_join.ws_net_profit BETWEEN 150 AND 300) OR (customer_address_filter.ca_state IN ('KS','OH','VA') AND web_sales_join.ws_net_profit BETWEEN 50 AND 250))) SELECT SUBSTRING(r_reason_desc FROM 1 FOR 20), AVG(ws_quantity), AVG(wr_refunded_cash), AVG(wr_fee) FROM web_returns_join GROUP BY r_reason_desc ORDER BY SUBSTRING(r_reason_desc FROM 1 FOR 20), AVG(ws_quantity), AVG(wr_refunded_cash), AVG(wr_fee) LIMIT 100