SELECT COUNT(*) FROM ((SELECT DISTINCT c_last_name, c_first_name, d_date FROM store_sales JOIN date_dim ON store_sales.ss_sold_date_sk = date_dim.d_date_sk JOIN customer ON store_sales.ss_customer_sk = customer.c_customer_sk WHERE d_month_seq BETWEEN 1214 AND 1214+11 AND ss_list_price BETWEEN 242 AND 271 AND c_birth_year BETWEEN 1955 AND 1961 AND ss_wholesale_cost BETWEEN 35 AND 45) EXCEPT (SELECT DISTINCT c_last_name, c_first_name, d_date FROM catalog_sales JOIN date_dim ON catalog_sales.cs_sold_date_sk = date_dim.d_date_sk JOIN customer ON catalog_sales.cs_bill_customer_sk = customer.c_customer_sk WHERE d_month_seq BETWEEN 1214 AND 1214+11 AND cs_list_price BETWEEN 242 AND 271 AND c_birth_year BETWEEN 1955 AND 1961 AND cs_wholesale_cost BETWEEN 35 AND 45) EXCEPT (SELECT DISTINCT c_last_name, c_first_name, d_date FROM web_sales JOIN date_dim ON web_sales.ws_sold_date_sk = date_dim.d_date_sk JOIN customer ON web_sales.ws_bill_customer_sk = customer.c_customer_sk WHERE d_month_seq BETWEEN 1214 AND 1214+11 AND ws_list_price BETWEEN 242 AND 271 AND c_birth_year BETWEEN 1955 AND 1961 AND ws_wholesale_cost BETWEEN 35 AND 45)) cool_cust