WITH filtered_dates AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_month_seq BETWEEN 1213 AND 1224), filtered_customers AS (SELECT c_customer_sk, c_last_name, c_first_name FROM customer WHERE c_birth_year BETWEEN 1968 AND 1974), sales_union AS ((SELECT 'store' AS channel, fc.c_last_name, fc.c_first_name, fd.d_date FROM filtered_dates fd INNER JOIN store_sales ss ON ss.ss_sold_date_sk = fd.d_date_sk INNER JOIN filtered_customers fc ON ss.ss_customer_sk = fc.c_customer_sk WHERE ss.ss_list_price BETWEEN 168 AND 197 AND ss.ss_wholesale_cost BETWEEN 76 AND 86) UNION ALL (SELECT 'catalog' AS channel, fc.c_last_name, fc.c_first_name, fd.d_date FROM filtered_dates fd INNER JOIN catalog_sales cs ON cs.cs_sold_date_sk = fd.d_date_sk INNER JOIN filtered_customers fc ON cs.cs_bill_customer_sk = fc.c_customer_sk WHERE cs.cs_list_price BETWEEN 168 AND 197 AND cs.cs_wholesale_cost BETWEEN 76 AND 86) UNION ALL (SELECT 'web' AS channel, fc.c_last_name, fc.c_first_name, fd.d_date FROM filtered_dates fd INNER JOIN web_sales ws ON ws.ws_sold_date_sk = fd.d_date_sk INNER JOIN filtered_customers fc ON ws.ws_bill_customer_sk = fc.c_customer_sk WHERE ws.ws_list_price BETWEEN 168 AND 197 AND ws.ws_wholesale_cost BETWEEN 76 AND 86)), grouped_results AS (SELECT c_last_name, c_first_name, d_date, COUNT(CASE WHEN channel = 'store' THEN 1 END) AS store_count, COUNT(CASE WHEN channel = 'catalog' THEN 1 END) AS catalog_count, COUNT(CASE WHEN channel = 'web' THEN 1 END) AS web_count FROM sales_union GROUP BY c_last_name, c_first_name, d_date), filter_present_only_in_store AS (SELECT c_last_name, c_first_name, d_date FROM grouped_results WHERE store_count > 0 AND catalog_count = 0 AND web_count = 0) SELECT COUNT(*) FROM filter_present_only_in_store