WITH fact_core AS ( SELECT ss_customer_sk FROM store_sales INNER JOIN store_returns ON ss_ticket_number = sr_ticket_number AND ss_item_sk = sr_item_sk INNER JOIN web_sales ON ss_customer_sk = ws_bill_customer_sk AND ss_item_sk = ws_item_sk INNER JOIN date_dim d1 ON sr_returned_date_sk = d1.d_date_sk INNER JOIN date_dim d2 ON ws_sold_date_sk = d2.d_date_sk INNER JOIN item ON i_item_sk = ss_item_sk WHERE i_category IN ('Children', 'Electronics', 'Music') AND d2.d_date BETWEEN d1.d_date AND (d1.d_date + INTERVAL '90 DAY') AND d1.d_year = 1998 AND ss_sales_price / ss_list_price BETWEEN 35 * 0.01 AND 55 * 0.01 ), aggregation_core AS ( SELECT ss_customer_sk, COUNT(*) AS cnt FROM fact_core GROUP BY ss_customer_sk ), late_dim_join AS ( SELECT c_customer_sk, c_first_name, c_last_name, cnt FROM aggregation_core INNER JOIN customer ON ss_customer_sk = c_customer_sk INNER JOIN customer_address ON c_current_addr_sk = ca_address_sk INNER JOIN household_demographics ON c_current_hdemo_sk = hd_demo_sk WHERE ca_state IN ('AR', 'CO', 'NC', 'NY', 'TX') AND hd_income_band_sk BETWEEN 7 AND 13 AND hd_buy_potential = '0-500' ) SELECT c_customer_sk, c_first_name, c_last_name, cnt FROM late_dim_join ORDER BY cnt