WITH filtered_d1 AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_year = 2000), filtered_d2 AS (SELECT d_date_sk, d_date FROM date_dim d2 WHERE EXISTS (SELECT 1 FROM filtered_d1 d1 WHERE d2.d_date BETWEEN d1.d_date AND (d1.d_date + INTERVAL '90 DAY'))), filtered_item AS (SELECT i_item_sk FROM item WHERE i_category IN ('Children', 'Home', 'Women')), fact_core AS (SELECT ss_customer_sk FROM store_sales INNER JOIN store_returns ON store_sales.ss_ticket_number = store_returns.sr_ticket_number AND store_sales.ss_item_sk = store_returns.sr_item_sk INNER JOIN filtered_item ON store_sales.ss_item_sk = filtered_item.i_item_sk INNER JOIN filtered_d1 ON store_returns.sr_returned_date_sk = filtered_d1.d_date_sk INNER JOIN web_sales ON store_returns.sr_item_sk = web_sales.ws_item_sk AND store_sales.ss_customer_sk = web_sales.ws_bill_customer_sk INNER JOIN filtered_d2 ON web_sales.ws_sold_date_sk = filtered_d2.d_date_sk WHERE store_sales.ss_sales_price / store_sales.ss_list_price BETWEEN 76 * 0.01 AND 96 * 0.01), aggregation AS (SELECT ss_customer_sk, COUNT(*) AS cnt FROM fact_core GROUP BY ss_customer_sk), customer_decorate AS (SELECT c_customer_sk, c_first_name, c_last_name, aggregation.cnt FROM aggregation INNER JOIN customer ON aggregation.ss_customer_sk = customer.c_customer_sk INNER JOIN customer_address ON customer.c_current_addr_sk = customer_address.ca_address_sk INNER JOIN household_demographics ON customer.c_current_hdemo_sk = household_demographics.hd_demo_sk WHERE customer_address.ca_state IN ('AR', 'GA', 'IN', 'KY', 'VA') AND household_demographics.hd_income_band_sk BETWEEN 8 AND 14 AND household_demographics.hd_buy_potential = '501-1000') SELECT c_customer_sk, c_first_name, c_last_name, cnt FROM customer_decorate ORDER BY cnt