WITH dim_item AS (SELECT i_item_sk FROM item WHERE i_category IN ('Men', 'Shoes', 'Sports') AND i_manager_id IN (6, 11, 16, 17, 19, 28, 47, 82, 88, 98)), dim_customer AS (SELECT c_customer_sk, cd_gender, cd_marital_status, cd_education_status, hd_vehicle_count FROM customer JOIN customer_address ON c_current_addr_sk = ca_address_sk JOIN household_demographics ON c_current_hdemo_sk = hd_demo_sk JOIN customer_demographics ON c_current_cdemo_sk = cd_demo_sk WHERE ca_state IN ('AR', 'CA', 'KS', 'NY', 'VA')), dim_date_1998 AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_year = 1998), fact_joins AS (SELECT dim_customer.cd_gender, dim_customer.cd_marital_status, dim_customer.cd_education_status, dim_customer.hd_vehicle_count FROM web_sales JOIN store_sales ON ws_item_sk = ss_item_sk AND ws_bill_customer_sk = ss_customer_sk JOIN dim_item ON ss_item_sk = dim_item.i_item_sk JOIN dim_customer ON ss_customer_sk = dim_customer.c_customer_sk JOIN dim_date_1998 d1 ON ss_sold_date_sk = d1.d_date_sk JOIN date_dim d2 ON ws_sold_date_sk = d2.d_date_sk JOIN warehouse ON ws_warehouse_sk = w_warehouse_sk JOIN store ON s_state = w_state JOIN inventory ON inv_warehouse_sk = ws_warehouse_sk AND inv_item_sk = ss_item_sk AND inv_date_sk = ss_sold_date_sk WHERE d2.d_date BETWEEN d1.d_date AND (d1.d_date + INTERVAL '30 day') AND inv_quantity_on_hand >= ss_quantity AND ws_wholesale_cost BETWEEN 76 AND 96) SELECT cd_gender, cd_marital_status, cd_education_status, hd_vehicle_count, COUNT(*) AS cnt FROM fact_joins GROUP BY cd_gender, cd_marital_status, cd_education_status, hd_vehicle_count ORDER BY cnt