WITH sd_stores AS (SELECT s_store_sk FROM store WHERE s_state = 'SD'), customer_total_return AS (SELECT sr_customer_sk AS ctr_customer_sk, sr_store_sk AS ctr_store_sk, SUM(SR_FEE) AS ctr_total_return FROM store_returns, date_dim, sd_stores WHERE sr_returned_date_sk = d_date_sk AND d_year = 2000 AND sr_store_sk = sd_stores.s_store_sk GROUP BY sr_customer_sk, sr_store_sk), store_avg AS (SELECT ctr_store_sk, AVG(ctr_total_return) * 1.2 AS avg_threshold FROM customer_total_return GROUP BY ctr_store_sk) SELECT c_customer_id FROM customer_total_return ctr1, customer, store_avg WHERE ctr1.ctr_store_sk = store_avg.ctr_store_sk AND ctr1.ctr_total_return > store_avg.avg_threshold AND ctr1.ctr_customer_sk = c_customer_sk ORDER BY c_customer_id LIMIT 100