WITH store_filtered AS (SELECT s_store_sk FROM store WHERE s_state = 'SD'), date_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000), filtered_returns AS (SELECT sr_customer_sk, sr_store_sk, sr_fee FROM store_returns AS sr JOIN store_filtered AS sf ON sr.sr_store_sk = sf.s_store_sk JOIN date_filtered AS df ON sr.sr_returned_date_sk = df.d_date_sk), customer_total_return AS (SELECT sr_customer_sk AS ctr_customer_sk, sr_store_sk AS ctr_store_sk, SUM(sr_fee) AS ctr_total_return FROM filtered_returns GROUP BY sr_customer_sk, sr_store_sk), store_avg_return AS (SELECT ctr_store_sk, AVG(ctr_total_return) * 1.2 AS avg_threshold FROM customer_total_return GROUP BY ctr_store_sk)
SELECT c.c_customer_id FROM customer_total_return AS ctr1 JOIN store_avg_return AS sar ON ctr1.ctr_store_sk = sar.ctr_store_sk JOIN customer AS c ON ctr1.ctr_customer_sk = c.c_customer_sk WHERE ctr1.ctr_total_return > sar.avg_threshold ORDER BY c.c_customer_id LIMIT 100