WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy BETWEEN 1 AND 4), filtered_customers_base AS (SELECT DISTINCT c.c_customer_sk, c.c_current_cdemo_sk, cd.cd_gender, cd.cd_marital_status, cd.cd_education_status, cd.cd_purchase_estimate, cd.cd_credit_rating, cd.cd_dep_count, cd.cd_dep_employed_count, cd.cd_dep_college_count FROM customer AS c JOIN customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN customer_demographics AS cd ON cd.cd_demo_sk = c.c_current_cdemo_sk WHERE ca.ca_county IN ('Storey County', 'Marquette County', 'Warren County', 'Cochran County', 'Kandiyohi County')), customers_with_store_sales AS (SELECT fc.* FROM filtered_customers_base AS fc WHERE EXISTS(SELECT 1 FROM store_sales AS ss JOIN filtered_dates AS fd ON ss.ss_sold_date_sk = fd.d_date_sk WHERE ss.ss_customer_sk = fc.c_customer_sk)), customers_with_web_sales AS (SELECT fc.* FROM filtered_customers_base AS fc WHERE EXISTS(SELECT 1 FROM web_sales AS ws JOIN filtered_dates AS fd ON ws.ws_sold_date_sk = fd.d_date_sk WHERE ws.ws_bill_customer_sk = fc.c_customer_sk)), customers_with_catalog_sales AS (SELECT fc.* FROM filtered_customers_base AS fc WHERE EXISTS(SELECT 1 FROM catalog_sales AS cs JOIN filtered_dates AS fd ON cs.cs_sold_date_sk = fd.d_date_sk WHERE cs.cs_ship_customer_sk = fc.c_customer_sk)), union_customers AS (SELECT * FROM customers_with_store_sales WHERE c_customer_sk IN (SELECT c_customer_sk FROM customers_with_web_sales) UNION SELECT * FROM customers_with_store_sales WHERE c_customer_sk IN (SELECT c_customer_sk FROM customers_with_catalog_sales))
SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3, cd_dep_count, COUNT(*) AS cnt4, cd_dep_employed_count, COUNT(*) AS cnt5, cd_dep_college_count, COUNT(*) AS cnt6 FROM union_customers GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count LIMIT 100