WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001), filtered_demographics AS (SELECT cd_demo_sk FROM customer_demographics WHERE (cd_marital_status = 'D' AND cd_education_status = 'Unknown') OR (cd_marital_status = 'S' AND cd_education_status = 'College') OR (cd_marital_status = 'M' AND cd_education_status = '4 yr Degree')), filtered_household AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_dep_count IN (1, 3)), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_country = 'United States' AND (ca_state IN ('SD', 'KS', 'MI') OR ca_state IN ('MO', 'ND', 'CO') OR ca_state IN ('NH', 'OH', 'TX')))
SELECT AVG(ss_quantity), AVG(ss_ext_sales_price), AVG(ss_ext_wholesale_cost), SUM(ss_ext_wholesale_cost) FROM store_sales AS ss JOIN store AS s ON s.s_store_sk = ss.ss_store_sk JOIN filtered_date AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_demographics AS cd ON ss.ss_cdemo_sk = cd.cd_demo_sk JOIN filtered_household AS hd ON ss.ss_hdemo_sk = hd.hd_demo_sk JOIN filtered_address AS ca ON ss.ss_addr_sk = ca.ca_address_sk WHERE ((cd.cd_marital_status = 'D' AND cd.cd_education_status = 'Unknown' AND ss.ss_sales_price BETWEEN 100.00 AND 150.00 AND hd.hd_dep_count = 3) OR (cd.cd_marital_status = 'S' AND cd.cd_education_status = 'College' AND ss.ss_sales_price BETWEEN 50.00 AND 100.00 AND hd.hd_dep_count = 1) OR (cd.cd_marital_status = 'M' AND cd.cd_education_status = '4 yr Degree' AND ss.ss_sales_price BETWEEN 150.00 AND 200.00 AND hd.hd_dep_count = 1)) AND ((ca.ca_state IN ('SD', 'KS', 'MI') AND ss.ss_net_profit BETWEEN 100 AND 200) OR (ca.ca_state IN ('MO', 'ND', 'CO') AND ss.ss_net_profit BETWEEN 150 AND 300) OR (ca.ca_state IN ('NH', 'OH', 'TX') AND ss.ss_net_profit BETWEEN 50 AND 250))