WITH d1_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name = '2001Q1'), filtered_store_sales AS (SELECT ss_customer_sk, ss_item_sk, ss_ticket_number, ss_store_sk, ss_quantity FROM store_sales JOIN d1_filtered ON ss_sold_date_sk = d_date_sk), d2_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name IN ('2001Q1', '2001Q2', '2001Q3')), filtered_store_returns AS (SELECT sr_customer_sk, sr_item_sk, sr_ticket_number, sr_return_quantity FROM store_returns JOIN d2_filtered ON sr_returned_date_sk = d_date_sk), d3_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name IN ('2001Q1', '2001Q2', '2001Q3')), filtered_catalog_sales AS (SELECT cs_bill_customer_sk, cs_item_sk, cs_quantity FROM catalog_sales JOIN d3_filtered ON cs_sold_date_sk = d_date_sk)
SELECT i_item_id, i_item_desc, s_state, COUNT(ss_quantity) AS store_sales_quantitycount, AVG(ss_quantity) AS store_sales_quantityave, STDDEV_SAMP(ss_quantity) AS store_sales_quantitystdev, STDDEV_SAMP(ss_quantity) / AVG(ss_quantity) AS store_sales_quantitycov, COUNT(sr_return_quantity) AS store_returns_quantitycount, AVG(sr_return_quantity) AS store_returns_quantityave, STDDEV_SAMP(sr_return_quantity) AS store_returns_quantitystdev, STDDEV_SAMP(sr_return_quantity) / AVG(sr_return_quantity) AS store_returns_quantitycov, COUNT(cs_quantity) AS catalog_sales_quantitycount, AVG(cs_quantity) AS catalog_sales_quantityave, STDDEV_SAMP(cs_quantity) AS catalog_sales_quantitystdev, STDDEV_SAMP(cs_quantity) / AVG(cs_quantity) AS catalog_sales_quantitycov FROM filtered_store_sales AS ss JOIN store ON s_store_sk = ss_store_sk JOIN item AS i ON i_item_sk = ss_item_sk LEFT JOIN filtered_store_returns AS sr ON ss.ss_customer_sk = sr.sr_customer_sk AND ss.ss_item_sk = sr.sr_item_sk AND ss.ss_ticket_number = sr.sr_ticket_number LEFT JOIN filtered_catalog_sales AS cs ON (sr.sr_customer_sk = cs.cs_bill_customer_sk AND sr.sr_item_sk = cs.cs_item_sk) OR (ss.ss_customer_sk = cs.cs_bill_customer_sk AND ss.ss_item_sk = cs.cs_item_sk) GROUP BY i_item_id, i_item_desc, s_state HAVING COUNT(ss_quantity) > 0 AND (COUNT(sr_return_quantity) > 0 OR COUNT(cs_quantity) > 0) ORDER BY i_item_id, i_item_desc, s_state LIMIT 100