WITH date_filter AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy BETWEEN 1 AND 4), filtered_store_sales AS (SELECT ss_customer_sk FROM store_sales JOIN date_filter ON ss_sold_date_sk = d_date_sk), filtered_web_sales AS (SELECT ws_bill_customer_sk FROM web_sales JOIN date_filter ON ws_sold_date_sk = d_date_sk), filtered_catalog_sales AS (SELECT cs_ship_customer_sk FROM catalog_sales JOIN date_filter ON cs_sold_date_sk = d_date_sk), customer_address_filtered AS (SELECT ca_address_sk FROM customer_address WHERE ca_county IN ('Storey County', 'Marquette County', 'Warren County', 'Cochran County', 'Kandiyohi County'))
SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3, cd_dep_count, COUNT(*) AS cnt4, cd_dep_employed_count, COUNT(*) AS cnt5, cd_dep_college_count, COUNT(*) AS cnt6 FROM customer AS c JOIN customer_address_filtered AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN customer_demographics AS cd ON cd.cd_demo_sk = c.c_current_cdemo_sk WHERE EXISTS(SELECT 1 FROM filtered_store_sales AS ss WHERE ss.ss_customer_sk = c.c_customer_sk) AND (EXISTS(SELECT 1 FROM filtered_web_sales AS ws WHERE ws.ws_bill_customer_sk = c.c_customer_sk) OR EXISTS(SELECT 1 FROM filtered_catalog_sales AS cs WHERE cs.cs_ship_customer_sk = c.c_customer_sk)) GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count LIMIT 100