WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998), filtered_cd1 AS (SELECT cd_demo_sk, cd_dep_count FROM customer_demographics WHERE cd_gender = 'F' AND cd_education_status = 'Advanced Degree'), filtered_customer AS (SELECT c_customer_sk, c_current_cdemo_sk, c_current_addr_sk, c_birth_year FROM customer WHERE c_birth_month IN (10, 7, 8, 4, 1, 2)), filtered_ca AS (SELECT ca_address_sk, ca_country, ca_state, ca_county FROM customer_address WHERE ca_state IN ('WA', 'GA', 'NC', 'ME', 'WY', 'OK', 'IN')), prefiltered_sales AS (SELECT cs_item_sk, cs_bill_cdemo_sk, cs_bill_customer_sk, cs_quantity, cs_list_price, cs_coupon_amt, cs_sales_price, cs_net_profit FROM catalog_sales JOIN filtered_dates ON cs_sold_date_sk = d_date_sk)
SELECT i_item_id, fca.ca_country, fca.ca_state, fca.ca_county, AVG(CAST(cs_quantity AS DECIMAL(12, 2))) AS agg1, AVG(CAST(cs_list_price AS DECIMAL(12, 2))) AS agg2, AVG(CAST(cs_coupon_amt AS DECIMAL(12, 2))) AS agg3, AVG(CAST(cs_sales_price AS DECIMAL(12, 2))) AS agg4, AVG(CAST(cs_net_profit AS DECIMAL(12, 2))) AS agg5, AVG(CAST(fc.c_birth_year AS DECIMAL(12, 2))) AS agg6, AVG(CAST(fcd1.cd_dep_count AS DECIMAL(12, 2))) AS agg7 FROM prefiltered_sales AS ps JOIN filtered_cd1 AS fcd1 ON ps.cs_bill_cdemo_sk = fcd1.cd_demo_sk JOIN filtered_customer AS fc ON ps.cs_bill_customer_sk = fc.c_customer_sk JOIN filtered_ca AS fca ON fc.c_current_addr_sk = fca.ca_address_sk JOIN customer_demographics AS cd2 ON fc.c_current_cdemo_sk = cd2.cd_demo_sk JOIN item AS i ON ps.cs_item_sk = i.i_item_sk GROUP BY ROLLUP (i_item_id, fca.ca_country, fca.ca_state, fca.ca_county) ORDER BY fca.ca_country, fca.ca_state, fca.ca_county, i_item_id LIMIT 100