WITH filtered_date_range AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2002-02-26' AND (CAST('2002-02-26' AS DATE) + INTERVAL '90 DAY')), avg_discount AS (SELECT 1.3 * AVG(ws_ext_discount_amt) AS threshold FROM web_sales AS ws INNER JOIN filtered_date_range AS fdr ON ws.ws_sold_date_sk = fdr.d_date_sk WHERE ws.ws_item_sk = (SELECT i_item_sk FROM item WHERE i_manufact_id = 320 LIMIT 1)) SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM web_sales INNER JOIN item ON i_item_sk = ws_item_sk INNER JOIN date_dim ON d_date_sk = ws_sold_date_sk WHERE i_manufact_id = 320 AND d_date BETWEEN '2002-02-26' AND (CAST('2002-02-26' AS DATE) + INTERVAL '90 DAY') AND ws_ext_discount_amt > (SELECT threshold FROM avg_discount) ORDER BY SUM(ws_ext_discount_amt) LIMIT 100;