WITH date_filtered_sales AS (SELECT ws_item_sk, ws_ext_discount_amt FROM web_sales JOIN date_dim ON d_date_sk = ws_sold_date_sk WHERE d_date BETWEEN '2002-02-26' AND (CAST('2002-02-26' AS DATE) + INTERVAL '90 DAY')) SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM date_filtered_sales AS dfs INNER JOIN item ON i_item_sk = dfs.ws_item_sk WHERE i_manufact_id = 320 AND dfs.ws_ext_discount_amt > (SELECT 1.3 * AVG(ws_ext_discount_amt) FROM date_filtered_sales WHERE ws_item_sk = i_item_sk) ORDER BY SUM(ws_ext_discount_amt) LIMIT 100;